#include <errno.h>
#include <stdint.h>
#include <stddef.h> // for ptrdiff_t

static uint8_t *__sbrk_heap_end = NULL;

void *_sbrk(ptrdiff_t incr)
{
    extern uint8_t _end;           /* Symbol from linker script */
    extern uint8_t _estack;        /* RAM end */
    extern uint32_t _Min_Stack_Size; /* Reserved stack size */

    const uint32_t stack_limit = (uint32_t)&_estack - (uint32_t)&_Min_Stack_Size;
    const uint8_t *max_heap = (uint8_t *)stack_limit;
    uint8_t *prev_heap_end;

    /* Initialize heap pointer on first call */
    if (__sbrk_heap_end == NULL)
    {
        __sbrk_heap_end = &_end;
    }

    /* Check if new allocation would collide with stack */
    if (__sbrk_heap_end + incr > max_heap)
    {
        errno = ENOMEM;
        return (void *)-1;
    }

    prev_heap_end = __sbrk_heap_end;
    __sbrk_heap_end += incr;

    return (void *)prev_heap_end;
}
